mapper.select:
SELECT
	*
FROM
	periodtime_close
WHERE
	venues_id = #{venues_id}
AND
	close_date = #{close_date}
AND
	close_time = #{close_time}

mapper.delete:
		delete
		FROM
			periodtime_close
        WHERE
			venues_id = #{venues_id}
		AND
			close_date = #{close_date}
		AND
			close_time = #{close_time}

mapper.update:
UPDATE periodtime
betTime=#{betTime},
sort=#{sort},
price=#{price},
weekPrice=#{weekPrice}
WHERE pid = #{pid}

mapper.select:
		SELECT
			o.*,(select h.name from vhouse h where h.id in(select p.groups from venues p where p.vid = o.vid)) as changguan_name,(select p.vname from venues p where p.vid = o.vid) as pianchang_name
		FROM
			zc_order o
		WHERE
			DATE_FORMAT(o.reserveTime,'%Y%m%d') between #{start_date} and #{end_date}
		AND
			o.type >= #{orderTypeReachAlipay}

		order by o.oid desc
		limit #{page},#{pageSize}

		SELECT
			o.*,(select h.name from vhouse h where h.id in(select p.groups from venues p where p.vid = o.vid)) as changguan_name,(select p.vname from venues p where p.vid = o.vid) as pianchang_name
		FROM
			zc_order o
		WHERE
			o.type = #{type}
		AND
			o.reserveTime between #{start_date} and #{end_date}
		order by o.oid desc
		limit #{page},#{pageSize}
